"use strict";function Workers(t){this._el=document.querySelectorAll(t.el),this._target=t.target,this._widthPosition=t.widthPosition;for(var e=0;e<this._el.length;e++)this._el[e].addEventListener("mouseover",this.show.bind(this)),this._el[e].addEventListener("mouseout",this.hide.bind(this))}Workers.prototype.show=function(t){var e=t.currentTarget.querySelector(this._target);e.classList.add("show")},Workers.prototype.hide=function(t){var e=t.currentTarget.querySelector(this._target);e.classList.remove("show")};var workers=new Workers({el:".team__item",target:".team__info"});$(document).ready(function(){$(".js-slider").slick({autoplay:!0,arrows:!1,dots:!0})});